import React, { PropTypes as PT } from 'react';
import classNames from 'classnames';
import { Normaltekst } from 'nav-frontend-typografi';
import 'nav-frontend-etiketter-style';// eslint-disable-line import/extensions

const cls = (type, className) => classNames('etikett', className, {
    'etikett--advarsel': type === 'advarsel',
    'etikett--fokus': type === 'fokus',
    'etikett--suksess': type === 'suksess',
    'etikett--info': type === 'info'
});

function EtikettBase({ type, className, children, ...props }) {
    return (
        <div className={cls(type, className)} {...props}>
            <Normaltekst tag="span">{children}</Normaltekst>
        </div>
    );
}

EtikettBase.propTypes = {
    children: PT.node.isRequired,
    type: PT.oneOf(['advarsel', 'fokus', 'suksess', 'info']).isRequired,
    className: PT.string
};

EtikettBase.defaultProps = {
    className: undefined
};

export default EtikettBase;

export const EtikettAdvarsel = (props) => <EtikettBase type="advarsel" {...props} />;
export const EtikettFokus = (props) => <EtikettBase type="fokus" {...props} />;
export const EtikettSuksess = (props) => <EtikettBase type="suksess" {...props} />;
export const EtikettInfo = (props) => <EtikettBase type="info" {...props} />;
