import React, { Component, PropTypes as PT } from 'react';
import classNames from 'classnames';

const cls = (className) => classNames('row', className, {});

function Row({ children, className, ...props }) {

    return (
        <div className={cls(className)} {...props}>{children}</div>
    );
}

Row.propTypes = {
    className: PT.string
};

export default Row;
