import React, { PropTypes as PT } from 'react';
import classNames from 'classnames';
import 'nav-frontend-grid-style'; // eslint-disable-line import/extensions

const cls = (className) => classNames('row', className, {});

function Row({ children, className, ...props }) {
    return (
        <div className={cls(className)} {...props}>{children}</div>
    );
}

Row.defaultProps = {
    className: undefined
};

Row.propTypes = {
    className: PT.string,
    children: PT.oneOfType([
        PT.arrayOf(PT.node),
        PT.node
    ]).isRequired
};

export default Row;
