import React from 'react';
import { storiesOf } from '@kadira/storybook';
import { JSDokumentasjon } from './../../dokumentasjon';
import readme from './README.md';
import pkg from './package.json';
import { HjelpetekstOver, HjelpetekstUnder, HjelpetekstHoyre, HjelpetekstVenstre } from './src/hjelpetekst';
import Ikon from 'nav-frontend-ikoner-assets'; // eslint-disable-line import/extensions

const hjelpetekstStyle = {
    marginTop: 110,
    marginBottom: 100,
    paddingLeft: 400
};

const myAnchorStyle = {
    width: 24,
    height: 24
};

function MyAnchor(props) {
    return <Ikon kind={props.hover ? 'ok-sirkel-fylt' : 'ok-sirkel'} style={myAnchorStyle} />;
}

storiesOf('Hjelpetekst', module)
    .addWithSections('Plassert over', () => (
        <div style={hjelpetekstStyle}>
            <HjelpetekstOver anchor={MyAnchor}>
                Hjelpeteksten plassert over elementet som trenger forklaring.
            </HjelpetekstOver>
        </div>
    ), JSDokumentasjon(pkg, readme, HjelpetekstOver))
    .addWithSections('Plassert under', () => (
        <div style={hjelpetekstStyle}>
            <HjelpetekstUnder>
                Hjelpeteksten plassert under elementet som trenger forklaring.
            </HjelpetekstUnder>
        </div>
    ), JSDokumentasjon(pkg, readme, HjelpetekstUnder))
    .addWithSections('Plassert til høyre', () => (
        <div style={hjelpetekstStyle}>
            <HjelpetekstHoyre>
                Hjelpeteksten plassert til høyre for elementet som trenger forklaring.
            </HjelpetekstHoyre>
        </div>
    ), JSDokumentasjon(pkg, readme, HjelpetekstHoyre))
    .addWithSections('Plassert til venstre', () => (
        <div style={hjelpetekstStyle}>
            <HjelpetekstVenstre>
                Hjelpeteksten plassert til venstre for elementet som trenger forklaring.
            </HjelpetekstVenstre>
        </div>
    ), JSDokumentasjon(pkg, readme, HjelpetekstVenstre));
