import * as React from 'react';
import Ikon from 'nav-frontend-ikoner-assets';

import Ikonknapp from './ikonknapp';
import { IkonknappProps } from './ikonknapp';

class Søkeknapp extends React.Component<IkonknappProps> {
    render() {
        return (
        	<Ikonknapp
        		ikon={<Ikon kind="søk" />}
                kompakt
        		{...this.props}
        	>
                {this.props.children || 'Søk'}
            </Ikonknapp>
        );
    }
}

export default Søkeknapp;
