import React, { Component } from 'react';
import PT from 'prop-types';
import { Innholdstittel, Normaltekst } from 'nav-frontend-typografi';
import { HoyreChevron } from 'nav-frontend-chevron';

class SingleIngressInngang extends Component { // eslint-disable-line react/prefer-stateless-function
    render() {
        const { ingress, title, icon, ...props } = this.props;

        return (
            <div {... props} className="inngang inngang__singleingress">
                <i>{ icon }</i>
                <Innholdstittel>{ title }</Innholdstittel>
                <Normaltekst>{ ingress }</Normaltekst>
                <HoyreChevron stor />
            </div>
        );
    }
}

SingleIngressInngang.propTypes = {
    title: PT.string.isRequired,
    ingress: PT.string.isRequired,
    icon: PT.string.isRequired // todo: not string
};

export default SingleIngressInngang;
