import * as React from 'react';
import PT from 'prop-types';
import classNames from 'classnames';
import 'nav-frontend-lenker-style'; // eslint-disable-line import/extensions

const cls = (bla, hvit, hjorne, className) => classNames('lenke', className);

/**
 * Standard lenke
 */

const getRelFromTarget = (target) => {
    if (
    !target ||
    target.toLowerCase() === '_self' ||
    target.toLowerCase() === '_top' ||
    target.toLowerCase() === '_parent'
  ) {
        return null;
    }
    return 'noopener';
};

const Lenke = ({ href, target, ariaLabel, children, className }) => (
    <a
        href={href}
        target={target}
        rel={getRelFromTarget(target)}
        className={cls(className)}
        aria-label={ariaLabel}
    >
        {children}
    </a>
);

Lenke.defaultProps = {
    target: null,
    ariaLabel: null,
    className: ''
};

Lenke.propTypes = {
    href: PT.string.isRequired,
    children: PT.oneOfType([PT.node, PT.string]).isRequired,
    target: PT.string,
    ariaLabel: PT.string,
    className: PT.string
};

export default Lenke;
