import * as React from "react";
import * as classnames from "classnames";
import "nav-frontend-lenker-style";

export interface Props {
  href: string;
  target?: "string";
  ariaLabel?: string;
  className?: string;
}

const getRelFromTarget = target => {
  if (
    !target ||
    target.toLowerCase() === "_self" ||
    target.toLowerCase() === "_top" ||
    target.toLowerCase() === "_parent"
  ) {
    return undefined;
  }
  return "noopener";
};

const cls = className => classnames("lenke", className);

const Lenke: React.StatelessComponent<Props> = ({
  href,
  target,
  ariaLabel,
  children,
  className
}) => (
  <a
    href={href}
    target={target}
    rel={getRelFromTarget(target)}
    className={cls(className)}
    aria-label={ariaLabel}
  >
    {children}
  </a>
);

export default Lenke;
