import * as React from 'react';
import * as cn from 'classnames';
import Chevron from 'nav-frontend-chevron';

export interface LesMerToggleProps {
    erApen: boolean;
    onClick: (event: React.SyntheticEvent<HTMLButtonElement>) => void;
    lukkTekst: string;
    apneTekst: string;
}

const LesMerToggle: React.SFC<LesMerToggleProps> = ({ erApen, onClick, lukkTekst, apneTekst }) => {
    const btnClassName = cn('lesMerPanel__togglelink', {
        'lesMerPanel__togglelink--erApen': erApen
    });
    return (<div className="lesMerPanel__toggle">
        <button
            aria-pressed={erApen}
            onClick={onClick}
            className={btnClassName}
        >
            <Chevron type={erApen ? 'opp' : 'ned'} className="lesMerPanel__toggleChevron" />
            <span className="lesMerPanel__toggleTekst">
                { erApen ? lukkTekst : apneTekst }
            </span>
        </button>
    </div>);
};

export default LesMerToggle;
