import React, { Component, PropTypes as PT } from 'react';
import classNames from 'classnames';
import 'nav-frontend-lukknapp-style'; // eslint-disable-line import/extensions

const cls = (bla, hjorne, className) => classNames('lukknapp',
    {
        'lukknapp--bla': bla,
        'lukknapp--overstHjorne': hjorne
    },
    className
);

/**
 * Lukknapp
 */
class Lukknapp extends Component { // eslint-disable-line react/prefer-stateless-function
    render() {
        const { altTekst, bla, overstHjorne, className, ...props } = this.props;
        return (
            <button
                className={cls(bla, overstHjorne, className)}
                {...props}
            >
                <span className="text-hide">{altTekst}</span>
            </button>
        );
    }
}

Lukknapp.defaultProps = {
    altTekst: 'Lukk',
    bla: false,
    overstHjorne: false,
    className: ''
};

Lukknapp.propTypes = {
    /**
     * Tekst som beskriver knappen for skjermleser
     */
    altTekst: PT.string,
    /**
     * Bruk blå som standard farge i stedet for grå
     */
    bla: PT.bool,
    /**
     * Plasser knappen øverst i høyre hjørne.
     * Knappen er absolut possisjonert og trenger derfor litt hjelp med å legge seg riktig sted.
     */
    overstHjorne: PT.bool,
    className: PT.string
};

export default Lukknapp;
