import React, { Component } from 'react';
import { // eslint-disable-line import/no-extraneous-dependencies
    Innholdstittel,
    Normaltekst
} from 'nav-frontend-typografi';
import Modal from './src/index';

class ModalEksempel extends Component {
    constructor(props) {
        super(props);
        this.state = { isOpen: false };

        this.handleOpen = this.handleOpen.bind(this);
        this.handleClose = this.handleClose.bind(this);
    }

    handleOpen() {
        this.setState({ isOpen: true });
    }

    handleClose() {
        this.setState({ isOpen: false });
    }

    render() {
        return (
            <div>
                <Modal
                    className="begrenset-bredde"
                    contentLabel="contentlabel blir satt som aria-label"
                    isOpen={this.state.isOpen}
                    onRequestClose={this.handleClose}
                >
                    <Innholdstittel tag="h1" style={{ paddingRight: '3rem' }}>Min modal</Innholdstittel>
                    <Normaltekst>
                        Lorem ipsum dolor sit amet, consectetur adipisicing elit.
                        Accusamus eum ex facilis inventore minus, molestiae mollitia reiciendis ut veniam voluptatem!
                        Earum, id, qui. Autem doloribus, hic natus odio quae tempore.
                    </Normaltekst>

                    <Normaltekst>
                        Lorem ipsum dolor sit amet, consectetur adipisicing elit.
                        Accusamus eum ex facilis inventore minus, molestiae mollitia reiciendis ut veniam voluptatem!
                        Earum, id, qui. Autem doloribus, hic natus odio quae tempore.
                    </Normaltekst>
                </Modal>
                <button onClick={this.handleOpen}>Klikk for å åpne</button>
            </div>
        );
    }
}

export default ModalEksempel;
