import React, { Component } from 'react';
import { Innholdstittel } from 'nav-frontend-typografi'; // eslint-disable-line import/no-extraneous-dependencies
import Modal from './src/index';

class ModalEksempel extends Component {
    constructor(props) {
        super(props);
        this.state = { isOpen: false };

        this.handleOpen = this.handleOpen.bind(this);
        this.handleClose = this.handleClose.bind(this);
    }

    handleOpen() {
        this.setState({ isOpen: true });
    }

    handleClose() {
        this.setState({ isOpen: false });
    }

    render() {
        return (
            <div>
                <Modal
                    contentLabel="contentlabel blir satt som aria-label"
                    isOpen={this.state.isOpen}
                    onRequestClose={this.handleClose}
                >
                    <Innholdstittel tag="h1">Min modal</Innholdstittel>
                </Modal>
                <button onClick={this.handleOpen}>Klikk for å åpne</button>
            </div>
        );
    }
}

export default ModalEksempel;
