import React, { PropTypes as PT } from 'react';
import classNames from 'classnames';

export const cls = (className) => classNames('panel', className);

function PanelBase({ children, className, ...props }) {
    return (
        <div className={cls(className)} {...props}>{children}</div>
    );
}

PanelBase.propTypes = {
    className: PT.string,
    children: PT.node.isRequired
};
PanelBase.defaultProps = {
    className: undefined
};

export default PanelBase;

export const Panel = (props) => <PanelBase {...props} />;
export { default as EkspanderbartpanelPure } from './ekspanderbartpanel-pure';
export { default as Ekspanderbartpanel } from './ekspanderbartpanel';
