import React, { Component } from 'react';
// eslint-disable-next-line import/no-extraneous-dependencies
import Lenke from 'nav-frontend-lenker';
// eslint-disable-next-line
import Popover from './';

export default class PopoverNormalEksempel extends Component {

    constructor(props) {
        super(props);
        this.state = {
            anker: undefined
        };
    }

    render() {
        return (
            <div>
                <Lenke
                    href="#"
                    onClick={(e) => e.preventDefault()}
                    onMouseLeave={() => this.setState({ anker: undefined })}
                    onMouseEnter={(e) => this.setState({ anker: e.currentTarget })}
                >
                    Denne lenken
                </Lenke> toggler Popover ved
                <code className="inline">onMouseEnter</code>/
                <code className="inline">onMouseLeave</code>
                <Popover
                    ankerEl={this.state.anker}
                    scrollContainerSelector="article.mainContent"
                >
                    <p style={{ padding: '1rem' }}>
                        Dette er en popover.
                    </p>
                </Popover>
            </div>
        );
    }
}
