import React, { Component } from 'react';
// eslint-disable-next-line import/no-extraneous-dependencies
import { Knapp } from 'nav-frontend-knapper';
// eslint-disable-next-line
import Popover from './';

export default class PopoverNormalEksempel extends Component {

    constructor(props){
        super(props);
        this.state = {
            anker: undefined
        };
    }

    togglePopover = (anker) => {
        const newState = Object.assign({}, this.state);
        newState['anker'] = (newState['anker']) ? undefined : anker;
        this.setState(newState);
    }

    render() {
        return (
            <div>
                <Knapp onClick={(e) => this.togglePopover(e.currentTarget)}>Ã…pne popover</Knapp>
                <Popover
                    ankerEl={this.state.anker}
                    onClose={() => this.setState({anker: undefined})}
                    scrollContainerSelector="article.mainContent"
                >
                    <p style={{padding:'1rem'}}>
                        Dette er en popover.
                    </p>
                </Popover>
            </div>
        );
    }
}
