import React from 'react';
import {storiesOf} from '@kadira/storybook';
import './src/index.less';

storiesOf('Nav-frontend-skjema-style', module)
    .add('Skjemaelementer', () => (
        <fieldset className="skjema__fieldset">
            <legend className="skjema__legend">Dine fruktpreferanser</legend>

            <div className="skjema__sporsmal">Liker du frukt?</div>
            <div className=" skjema__input">
                <input className="radioknapp" type="radio" value="ja" name="liker-frukt" id="liker-ja"/>
                <label htmlFor="liker-ja">Ja</label>
            </div>
            <div className=" skjema__input">
                <input className="radioknapp" type="radio" value="nei" name="liker-frukt" id="liker-nei"/>
                <label htmlFor="liker-nei">Nei</label>
            </div>

            <div className="skjema__sporsmal">Hvilke frukt vil du ha?</div>
            <div className=" skjema__input">
                <input className="checkboks" type="checkbox" value="eple" name="frukt" id="eple"/>
                <label htmlFor="eple">Eple</label>
            </div>
            <div className=" skjema__input">
                <input className="checkboks" type="checkbox" value="banan" name="frukt" id="banan"/>
                <label htmlFor="banan">Banan</label>
            </div>
        </fieldset>
    ))
    .add('Checkbox', () => (
        <div>
            <div className=" skjema__input">
                <input className="checkboks" type="checkbox" value="eple" name="frukt" id="eple"/>
                <label htmlFor="eple">Eple</label>
            </div>
            <div className=" skjema__input">
                <input className="checkboks" type="checkbox" value="banan" name="frukt" id="banan"/>
                <label htmlFor="banan">Banan</label>
            </div>
        </div>
    ))
    .add('Radioknapp', () => (
        <div>
            <div className=" skjema__input">
                <input className="radioknapp" type="radio" value="ja" name="liker-frukt" id="liker-ja"/>
                <label htmlFor="liker-ja">Ja</label>
            </div>
            <div className=" skjema__input">
                <input className="radioknapp" type="radio" value="nei" name="liker-frukt" id="liker-nei"/>
                <label htmlFor="liker-nei">Nei</label>
            </div>
        </div>
    ));

