import React from 'react';
import {storiesOf} from '@kadira/storybook';
import './src/index.less';

storiesOf('Nav-frontend-skjema-style', module)
    .add('Checkbox', () => (
        <fieldset>
            <legend>Velg frukt</legend>
            <div className=" skjema__input">
                <input className="checkboks" type="checkbox" value="eple" name="frukt" id="eple"/>
                <label htmlFor="eple">Eple</label>
            </div>
            <div className=" skjema__input">
                <input className="checkboks" type="checkbox" value="kiwi" name="frukt" id="kiwi"/>
                <label htmlFor="kiwi">Kiwi</label>
            </div>
            <div className=" skjema__input">
                <input className="checkboks" type="checkbox" value="drue" name="frukt" id="drue"/>
                <label htmlFor="drue">Drue</label>
            </div>
        </fieldset>
    ))
    .add('Radioknapp', () => (
        <fieldset>
            <legend>Velg farge</legend>
            <div className=" skjema__input">
                <input className="radioknapp" type="radio" value="gul" name="farge" id="farge-gul"/>
                <label htmlFor="farge-gul">Gul</label>
            </div>
            <div className=" skjema__input">
                <input className="radioknapp" type="radio" value="hvit" name="farge" id="farge-hvit"/>
                <label htmlFor="farge-hvit">Hvit</label>
            </div>
            <div className=" skjema__input">
                <input className="radioknapp" type="radio" value="oransje" name="farge" id="farge-oransje"/>
                <label htmlFor="farge-oransje">Oransje</label>
            </div>
        </fieldset>
    ));

