import React, { Component } from 'react';
import Alertstripe from 'NavFrontendModules/nav-frontend-alertstriper';
// eslint-disable-next-line
import { FnrInput } from './';

export default class FnrInputExample extends Component {
    constructor(props) {
        super(props);
        this.state = { isFnrValid: false };
        this.updateValid = this.updateValid.bind(this);
    }

    updateValid(isValid) {
        this.setState({ isFnrValid: isValid });
    }

    render() {
        return (
            <div>
                { this.state.isFnrValid
                    ? (<Alertstripe type="suksess">Fødselsnummeret er riktig</Alertstripe>)
                    : (<Alertstripe type="feil">Feil fødselsnummer</Alertstripe>) }

                <FnrInput
                    bredde={'S'}
                    label="Fødselsnummer"
                    onChange={(e) => { console.log(e); }}
                    onValidate={this.updateValid}
                />
            </div>
        );
    }
}
