import React from 'react';
import { BekreftCheckboksPanel } from './../';
import { Checkbox } from "./..";

class BekreftCheckboksPanelExample extends React.Component {
    constructor(props) {
        super(props);
        this.state = { checked: false, feilmelding: false };
    }

    onChange(event) {
        // eslint-disable-next-line
        console.log('event', event);
        this.setState({ checked: !this.state.checked });
    }

	toggleFeil() {
		this.setState({ feilmelding: !this.state.feilmelding });
    }

    render() {
        return (
            <div>
                <BekreftCheckboksPanel
                    label="Ja, jeg samtykker"
                    checked={this.state.checked}
                    onChange={(event) => this.onChange(event)}
                    feil={this.state.feilmelding ? {feilmelding: "Samtykke er obligatorisk"} : undefined}
                >
                    <span>
                        For å gå videre må du samtykke til at vi får lov til å innhente og behandle persondata
                        om deg. Les mer om hvilke data vi henter og hvordan vi behandler disse dataene
                        i <a href="/lenke">vilkårene for denne tjenesten</a>.</span>
                </BekreftCheckboksPanel>
                <br/>

                <Checkbox
                    label="Feilmelding"
                    onChange={() => this.toggleFeil()}
                />
            </div>
        );
    }
}

export default BekreftCheckboksPanelExample;
