import React from 'react';
import { BekreftCheckboksPanel } from './../';

class BekreftCheckboksPanelExample extends React.Component {
    constructor(props) {
        super(props);
        this.state = { checked: false };
    }

    onChange(event) {
        // eslint-disable-next-line
        console.log('event', event);
        this.setState({ checked: !this.state.checked });
    }

    render() {
        return (
            <BekreftCheckboksPanel
                label="Ja"
                checked={this.state.checked}
                onChange={(event) => this.onChange(event)}
            >
                <span>Den klare luft, høylukten, blomsterduften, gangen, fuglekvitteret og de friske,
                    luftningene ved elven, virket i høy grad opplivende på <a href="/lenke">mitt sinn.</a></span>
            </BekreftCheckboksPanel>
        );
    }
}

export default BekreftCheckboksPanelExample;
