import React, { Component } from 'react';
import Alertstripe from 'NavFrontendModules/nav-frontend-alertstriper';
// eslint-disable-next-line
import { FnrInput } from '../';

export default class FnrInputExample extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value: '',
            valid: false
        };
    }

    render() {
        return (
            <div>
                <FnrInput
                    label="Fødselsnummer (11 siffer)"
                    value={this.state.value}
                    onChange={(e) => this.setState({value: e.target.value})}
                    onValidate={(valid) => this.setState({ valid })}
                    feil={(this.state.valid || !this.state.value ? undefined : 'Ugyldig fødselsnummer')}
                />
            </div>
        );
    }
}
