import * as React from 'react';
import * as classnames from 'classnames';
import { Undertittel } from 'nav-frontend-typografi';
import 'nav-frontend-skjema-style';

export interface FeiloppsummeringProps extends React.HTMLAttributes<HTMLDivElement> {
    /**
     * Egendefinert klassenavn.
     */
    className?: string;
    /**
     * Referanse til intern div
     */
    innerRef?: React.RefObject<HTMLDivElement>
}

class Feiloppsummering extends React.Component<FeiloppsummeringProps> {
    render(){
        const { children, className, innerRef, ...rest } = this.props;
        return (
            <div
                ref={innerRef}
                tabIndex={0}
                role="region"
                className={classnames('feiloppsummering', className)}
                {...rest}
            >
                {children}
            </div>
        );
    }
}

export default Feiloppsummering;