/* eslint-disable react/prefer-stateless-function  */
import React, { Component, PropTypes as PT } from 'react';
import { guid, autobind } from 'nav-frontend-js-utils';
import 'nav-frontend-skjema-style'; // eslint-disable-line import/extensions
import classNames from 'classnames';

const cls = (className) => classNames('skjemaelement skjemaelement--horisontal', className);

/**
 * Radio
 */
class Radio extends Component {

    constructor(props) {
        super(props);
        autobind(this);
    }

    focus() {
        this.radio.focus();
    }

    render() {
        const { className, name, label, id, ...other } = this.props;
        const inputId = id || guid();
        return (
            <div className={cls(className)}>
                <input
                    type="radio"
                    name={name}
                    className="skjemaelement__input radioknapp"
                    id={inputId}
                    ref={(radio) => { this.radio = radio; }}
                    {...other}
                />
                <label className="skjemaelement__label" htmlFor={inputId}>{label}</label>
            </div>
        );
    }
}

Radio.propTypes = {
    /**
     * ClassName
     */
    className: PT.string,
    /**
     * Label for radiobutton
     */
    label: PT.node.isRequired,
    /**
     * Id for radiobutton, hvis id ikke er satt brukes en tilfeldig guid
     */
    id: PT.string,
    /**
     * Name for radiobutton gruppe
     */
    name: PT.string.isRequired
};

Radio.defaultProps = {
    className: undefined,
    id: undefined
};

export default Radio;
