import * as PT from 'prop-types';
import * as React from 'react';
import * as classNames from 'classnames';
import 'nav-frontend-skjema-style';
import SkjemaelementFeilmelding, { SkjemaelementFeil, skjemaelementFeilmeldingShape } from './skjemaelement-feilmelding'; // tslint:disable-line:max-line-length

const cls = (className, harFeil) => classNames('skjemagruppe', className, {
    'skjemagruppe--feil': harFeil
});

export interface SkjemaGruppeProps {
    /**
     * En eller flere children, oftest en eller flere .skjemaelement
     */
    children: React.ReactNode | React.ReactNode[];
    /**
     * ClassName
     */
    className?: string;
    /**
     * Hvis skjemagruppen har feil sender man inn et objekt med en feilmelding
     */
    feil?: SkjemaelementFeil;
}

/**
 * Fieldset
 */
class SkjemaGruppe extends React.Component<SkjemaGruppeProps> {
    render() {
        const { children, className, feil, ...other } = this.props;
        return (
            <div className={cls(className, feil)} {...other}>
                {children}
                <SkjemaelementFeilmelding feil={feil} />
            </div>
        );
    }
}

(SkjemaGruppe as React.ComponentClass).propTypes = {
    /**
     * En eller flere children, oftest en eller flere .skjemaelement
     */
    children: PT.oneOfType([
        PT.arrayOf(PT.node),
        PT.node
    ]).isRequired,
    /**
     * ClassName
     */
    className: PT.string,
    /**
     * Hvis skjemagruppen har feil sender man inn et objekt med en feilmelding
     */
    feil: skjemaelementFeilmeldingShape
};

(SkjemaGruppe as React.ComponentClass).defaultProps = {
    className: undefined,
    feil: undefined
};

export default SkjemaGruppe;
