import React, { PropTypes as PT } from 'react';
import Ikoner from 'nav-frontend-ikoner-assets';
import classNames from 'classnames';
import 'nav-frontend-spinner-style';// eslint-disable-line import/extensions

const cls = (className, storrelse) => classNames('spinner', className, {
    [`spinner--${storrelse}`]: !!storrelse
});

function NavFrontendSpinner({ negativ, storrelse, className, ...props }) {
    const ikon = negativ ? 'spinner-negativ' : 'spinner';

    return (
        <Ikoner kind={ikon} className={cls(className, storrelse)} {...props} />
    );
}

NavFrontendSpinner.propTypes = {
    negativ: PT.bool,
    storrelse: PT.oneOf(['xxs', 'xs', 's', 'm', 'l', 'xl', 'xxl', 'xxxl']),
    className: PT.string,
    'aria-label': PT.string
};

NavFrontendSpinner.defaultProps = {
    'aria-label': 'Laster innhold',
    negativ: false,
    storrelse: 'm',
    className: undefined
};

export default NavFrontendSpinner;
